import React from 'react'
import { useState, useEffect } from "react";

function Table(props) {

    const [node, setNode] = useState({ key: "", value: "" })

    const setItemAndDisplayPopup = (event) => {

        const popupName = event.currentTarget.getAttribute('popup-name')

        if (popupName === "view") {
            const item = event.currentTarget.getAttribute('data-item')
            const itemJson = JSON.parse(item);
            setNode(prevItem => ({ ...prevItem, key: item.key, value: item.value }))
        }
        else if (popupName === "edit") {
            document.getElementById('confirm_product_delete_modal').showModal();
            const item = event.currentTarget.getAttribute('data-item')
            setNode(prevItem => ({ ...prevItem, key: item.key, value: item.value }))
        }
    };


    const rows = props.data.map((item, index) => (

        <tr key={index} className='hover'>
            <th>{index + 1}</th>
            <td>{item.key}</td>
            <td>{item.value}</td>
            <td>
                <button className="btn btn-xs btn-active" onClick={setItemAndDisplayPopup} data-item={JSON.stringify(item)} popup-name="view">View or Edit</button>

                <button type="button" class="btn btn-primary" onClick={setItemAndDisplayPopup} data-toggle="modal" data-item={JSON.stringify(item)} data-target="#exampleModalCenter" popup-name="view">
                   View or Edit
                </button>

            </td>

        </tr>

    ));

    return (
        <div><table class="table">
            <thead>
                <tr>
                    <th scope="col">#</th>
                    <th scope="col">Key</th>
                    <th scope="col">Value</th>
                    <th scope="col">Edit</th>
                    <th scope="col">View</th>
                </tr>
            </thead>
            <tbody>
                {rows}
            </tbody>
        </table>




            <div class="modal fade" id="exampleModalCenter" tabindex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
                <div class="modal-dialog modal-dialog-centered" role="document">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLongTitle">Modal title</h5>
                            <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div class="modal-body">

                        </div>
                        <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                            <button type="button" class="btn btn-primary">Save changes</button>
                        </div>
                    </div>
                </div>
            </div>


        </div>
    )
}

export default Table
